# Given two integer arrays nums1 and nums2, return the maximum length of a subarray that appears 
# in both arrays.
# https://leetcode.com/problems/maximum-length-of-repeated-subarray/
# day - 1


def findLength1(nums1, nums2):
    nums2_str = "".join([str(i) for i in nums2])
    max_str = ""
    res = 0
    for i in nums1:
        max_str += str(i)
        if max_str in nums2_str:
            res = max(res,len(max_str))
        else:
            max_str = max_str[1:]
    return res

nums1 = [1,2,3,2,1]
nums2 = [3,2,1,4,7]
print(findLength1(nums1, nums2))


def findLength(arr1, arr2):
    largest, count = 0,0
    i,j = 0, 0
    while i < len(arr1):
        j = i
        count = 0
        ptr = 0
        while j < len(arr1) and ptr < len(arr2):
            if arr1[j] == arr2[ptr]:
                count += 1
            else:
                count = 0
            j += 1
            ptr += 1
            largest = max(largest, count)
        i += 1
    
    i,j,count = 0,0,0
    while i < len(arr2):
        j = i
        count = 0
        ptr = 0
        while j < len(arr2) and ptr < len(arr1):
            if arr2[j] == arr1[ptr]:
                count += 1
            else:
                count = 0
            j += 1
            ptr += 1
            largest = max(largest, count)
        i += 1
    return largest



